import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Card,
  CardContent,
  Typography,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Alert,
} from '@mui/material';
import { Add as AddIcon, Delete as DeleteIcon } from '@mui/icons-material';
import axios from 'axios';

// Configure axios instance with base URL
const api = axios.create({
  baseURL: 'http://localhost:8000',
  headers: {
    'Content-Type': 'application/json',
  },
});

interface Course {
  id: number;
  name: string;
  code: string;
  grade_level: number;
  exclusivity_group?: number;
}

interface CourseGroup {
  id: number;
  name: string;
  courses: Course[];
}

export const CourseManagement: React.FC = () => {
  const [courses, setCourses] = useState<Course[]>([]);
  const [groups, setGroups] = useState<CourseGroup[]>([]);
  const [selectedGroup, setSelectedGroup] = useState<CourseGroup | null>(null);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [newGroupName, setNewGroupName] = useState('');
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    fetchCourses();
    fetchGroups();
  }, []);

  const fetchCourses = async () => {
    try {
      console.log('Fetching courses...');
      const response = await api.get('/scheduler/courses/');
      console.log('Courses response:', response.data);
      setCourses(response.data.courses);
    } catch (err) {
      setError('Failed to fetch courses');
      console.error('Error fetching courses:', err);
      if (axios.isAxiosError(err)) {
        console.error('Axios error details:', {
          message: err.message,
          response: err.response?.data,
          status: err.response?.status,
          headers: err.response?.headers
        });
      }
    }
  };

  const fetchGroups = async () => {
    try {
      console.log('Fetching groups...');
      const response = await api.get('/scheduler/course-groups/');
      console.log('Groups response:', response.data);
      setGroups(response.data.groups);
    } catch (err) {
      setError('Failed to fetch course groups');
      console.error('Error fetching groups:', err);
      if (axios.isAxiosError(err)) {
        console.error('Axios error details:', {
          message: err.message,
          response: err.response?.data,
          status: err.response?.status,
          headers: err.response?.headers
        });
      }
    }
  };

  const handleCreateGroup = async () => {
    try {
      await api.post('/scheduler/course-groups/', {
        name: newGroupName,
      });
      setDialogOpen(false);
      setNewGroupName('');
      fetchGroups();
    } catch (err) {
      setError('Failed to create group');
      console.error('Error creating group:', err);
    }
  };

  const handleDeleteGroup = async (groupId: number) => {
    try {
      await api.delete(`/scheduler/course-groups/${groupId}/`);
      fetchGroups();
    } catch (err) {
      setError('Failed to delete group');
      console.error('Error deleting group:', err);
    }
  };

  const handleAddCourseToGroup = async (courseId: number, groupId: number) => {
    try {
      await api.post(`/scheduler/course-groups/${groupId}/`, {
        course_ids: [courseId],
      });
      fetchGroups();
    } catch (err) {
      setError('Failed to add course to group');
      console.error('Error adding course to group:', err);
    }
  };

  const handleRemoveCourseFromGroup = async (courseId: number, groupId: number) => {
    try {
      await api.post(`/scheduler/course-groups/${groupId}/`, {
        course_ids: [],
      });
      fetchGroups();
    } catch (err) {
      setError('Failed to remove course from group');
      console.error('Error removing course from group:', err);
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Course Exclusivity Management
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }} onClose={() => setError(null)}>
          {error}
        </Alert>
      )}

      <Box sx={{ display: 'flex', gap: 2 }}>
        {/* Course Groups List */}
        <Card sx={{ minWidth: 300 }}>
          <CardContent>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography variant="h6">Course Groups</Typography>
              <IconButton onClick={() => setDialogOpen(true)}>
                <AddIcon />
              </IconButton>
            </Box>
            <List>
              {groups.map((group) => (
                <ListItem
                  key={group.id}
                  button
                  selected={selectedGroup?.id === group.id}
                  onClick={() => setSelectedGroup(group)}
                  secondaryAction={
                    <IconButton edge="end" onClick={() => handleDeleteGroup(group.id)}>
                      <DeleteIcon />
                    </IconButton>
                  }
                >
                  <ListItemText
                    primary={group.name}
                    secondary={`${group.courses.length} courses`}
                  />
                </ListItem>
              ))}
            </List>
          </CardContent>
        </Card>

        {/* Course List */}
        <Card sx={{ flex: 1 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              {selectedGroup ? `Courses in ${selectedGroup.name}` : 'All Courses'}
            </Typography>
            <List>
              {courses.map((course) => (
                <ListItem
                  key={course.id}
                  secondaryAction={
                    selectedGroup && (
                      course.exclusivity_group === selectedGroup.id ? (
                        <IconButton
                          edge="end"
                          onClick={() => handleRemoveCourseFromGroup(course.id, selectedGroup.id)}
                        >
                          <DeleteIcon />
                        </IconButton>
                      ) : (
                        <Button
                          variant="outlined"
                          onClick={() => handleAddCourseToGroup(course.id, selectedGroup.id)}
                        >
                          Add to Group
                        </Button>
                      )
                    )
                  }
                >
                  <ListItemText
                    primary={course.name}
                    secondary={`${course.code} - Grade ${course.grade_level}`}
                  />
                </ListItem>
              ))}
            </List>
          </CardContent>
        </Card>
      </Box>

      {/* New Group Dialog */}
      <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)}>
        <DialogTitle>Create New Course Group</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Group Name"
            fullWidth
            value={newGroupName}
            onChange={(e) => setNewGroupName(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleCreateGroup} variant="contained">
            Create
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};
